import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import TeamMemberList from './components/TeamMemberList';
import EventList from './components/EventList';
import VolunteerPointsList from './components/VolunteerPointsList';
import Login from './components/Login';
import styles from './Navbar.module.css'; // 确保CSS模块被使用

function App() {
    const [token, setToken] = useState(localStorage.getItem('token'));

    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
            setToken(storedToken);
        }
    }, []);

    if (!token) {
        return <Login setToken={setToken} />;
    }

    return (
        <Router>
            <nav className={styles.navbar}>  // 应用样式
                <img src="/pic/NYC.jpg" alt="Yacht Club Logo" className={styles.logo} />  // 应用样式
                <ul className={styles.navList}>  // 应用样式
                    <li><Link className={styles.navLink} to="/">Home</Link></li>  // 应用样式
                    <li><Link className={styles.navLink} to="/memberManagement">Member management</Link></li>  // 应用样式
                    <li><Link className={styles.navLink} to="/workTeamManagement">Work Team Management</Link></li>  // 应用样式
                    <li><Link className={styles.navLink} to="/userManagement">User Management</Link></li>  // 应用样式
                    <li><Link className={styles.navLink} to="/volunteer-points">Volunteer Points</Link></li>  // 应用样式
                    <li><Link className={styles.navLink} to="/events">Events</Link></li>  // 应用样式
                    <li><button className={styles.logoutButton} onClick={() => { localStorage.removeItem('token'); setToken(null); }}>Logout</button></li>  // 应用样式
                </ul>
            </nav>
            <Routes>
                <Route path="/" element={<TeamMemberList />} />
                <Route path="/memberManagement" element={<TeamMemberList />} />
                <Route path="/events" element={<EventList />} />
                <Route path="/volunteer-points" element={<VolunteerPointsList />} />
                <Route path="/login" element={<Login setToken={setToken} />} />
            </Routes>
        </Router>
    );
}

export default App;
