import React, { useState } from 'react';

const VolunteerForm = ({ addVolunteer }) => {
    const [name, setName] = useState('');
    const [activityType, setActivityType] = useState('');
    const [role, setRole] = useState('');
    const [hours, setHours] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const points = calculatePoints(activityType, role, hours);
        const newVolunteer = { name, activityType, role, hours, points };
        addVolunteer(newVolunteer);
        setName('');
        setActivityType('');
        setRole('');
        setHours('');
    };

    const calculatePoints = (activityType, role, hours) => {
        let points = 0;
        if (activityType === 'Off-Water') {
            if (role === 'Event Setup') {
                points = hours * 10; // Example: 10 points per hour for setup
            } else if (role === 'Administration') {
                points = hours * 8; // Example: 8 points per hour for admin
            }
        }
        return points;
    };

    return (
        <form className="volunteer-form" onSubmit={handleSubmit}>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Volunteer Name"
                required
            />
            <select
                value={activityType}
                onChange={(e) => setActivityType(e.target.value)}
                required
            >
                <option value="" disabled>Select Activity Type</option>
                <option value="Off-Water">Off-Water</option>
            </select>
            <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                required
            >
                <option value="" disabled>Select Role</option>
                <option value="Event Setup">Event Setup</option>
                <option value="Administration">Administration</option>
            </select>
            <input
                type="number"
                value={hours}
                onChange={(e) => setHours(e.target.value)}
                placeholder="Volunteer Hours"
                required
            />
            <button type="submit">Assign Points</button>
        </form>
    );
};

export default VolunteerForm;
