import React, { useState, useEffect } from 'react';
import './stylesAdd.css'; // Use the same CSS file for consistency

function AddVolunteerPoints() {
  const [members, setMembers] = useState([]); // To store members data
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredMembers, setFilteredMembers] = useState([]);
  const [volunteerDate, setVolunteerDate] = useState('');
  const [selectedMember, setSelectedMember] = useState(null);
  const [volunteerHours, setVolunteerHours] = useState('');
  const [volunteerPoints, setVolunteerPoints] = useState('');
  const [comments, setComments] = useState('');
  const [maintenanceTeam, setMaintenanceTeam] = useState('');
  const [maintenanceEvent, setMaintenanceEvent] = useState('');
  const [editorName, setEditorName] = useState('');
  const [editTime, setEditTime] = useState('');

  // Simulated list of admins and leaders
  const adminsAndLeaders = [
    'Admin1',
    'Leader1',
    'Admin2',
    'Leader2',
  ];

  useEffect(() => {
    // Simulated data for the demo
    const demoMembers = [
      { id: 1, firstName: 'John', lastName: 'Doe' },
      { id: 2, firstName: 'Jane', lastName: 'Smith' },
      { id: 3, firstName: 'Bob', lastName: 'Brown' },
    ];
    setMembers(demoMembers);
  }, []);

  useEffect(() => {
    setFilteredMembers(
      members.filter(member => 
        member.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        member.lastName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        member.id.toString().includes(searchQuery)
      )
    );
  }, [searchQuery, members]);

  const handleSelectMember = (member) => {
    setSelectedMember(member);
  };

  const handleSave = () => {
    if (selectedMember) {
      setEditTime(new Date().toLocaleString());
      console.log('Saving details for:', selectedMember);
      console.log('Date:', volunteerDate);
      console.log('Maintenance Team:', maintenanceTeam);
      console.log('Maintenance Event:', maintenanceEvent);
      console.log('Hours:', volunteerHours);
      console.log('Points:', volunteerPoints);
      console.log('Comments:', comments);
      console.log('Edited by:', editorName);
      console.log('Edit time:', editTime);
      // Here, you'd typically send this data to your backend for saving
    } else {
      console.log('Please select a member');
    }
  };

  const handleEdit = (member) => {
    console.log('Editing:', member);
    // Logic to handle edit, populate fields with member's data
  };

  const handleDelete = (memberId) => {
    if (window.confirm('Are you sure you want to delete this entry?')) {
      setFilteredMembers(filteredMembers.filter(member => member.id !== memberId));
      console.log('Deleted member with ID:', memberId);
    }
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <img src="nyclogo.jpg" alt="Nedlands Yacht Club Logo" className="logo" />
        <h1>Nedlands Yacht Club</h1>
      </nav>

      <div className="add-volunteer-container">
        {/* Search Bar */}
        <input 
          type="text" 
          placeholder="Search by ID or Name" 
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-bar"
        />

        {/* Members Table */}
        <table className="volunteer-table">
          <thead>
            <tr>
              <th>Select</th>
              <th>Member ID</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Date</th>
              <th>Maintenance Team</th>
              <th>Maintenance Event</th>
              <th>Volunteering Hours</th>
              <th>Points</th>
              <th>Comments</th>
              <th>Edited By</th>
              <th>Edit Time</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredMembers.map(member => (
              <tr key={member.id}>
                <td>
                  <input 
                    type="radio" 
                    name="selectedMember" 
                    onChange={() => handleSelectMember(member)}
                  />
                </td>
                <td>{member.id}</td>
                <td>{member.firstName}</td>
                <td>{member.lastName}</td>
                <td>
                  <input 
                    type="date" 
                    value={volunteerDate} 
                    onChange={(e) => setVolunteerDate(e.target.value)} 
                  />
                </td>
                <td>
                  <input 
                    type="text" 
                    placeholder="Team" 
                    value={maintenanceTeam}
                    onChange={(e) => setMaintenanceTeam(e.target.value)}
                  />
                </td>
                <td>
                  <input 
                    type="text" 
                    placeholder="Event" 
                    value={maintenanceEvent}
                    onChange={(e) => setMaintenanceEvent(e.target.value)}
                  />
                </td>
                <td>
                  <input 
                    type="number" 
                    placeholder="Hours" 
                    value={volunteerHours}
                    onChange={(e) => setVolunteerHours(e.target.value)}
                  />
                </td>
                <td>
                  <input 
                    type="number" 
                    placeholder="Points" 
                    value={volunteerPoints}
                    onChange={(e) => setVolunteerPoints(e.target.value)}
                  />
                </td>
                <td>
                  <input 
                    type="text" 
                    placeholder="Comments" 
                    value={comments}
                    onChange={(e) => setComments(e.target.value)}
                  />
                </td>
                <td>
                  <select 
                    value={editorName}
                    onChange={(e) => setEditorName(e.target.value)}
                  >
                    <option value="">Select Name</option>
                    {adminsAndLeaders.map(name => (
                      <option key={name} value={name}>{name}</option>
                    ))}
                  </select>
                </td>
                <td>{editTime}</td>
                <td>
                  <button onClick={() => handleEdit(member)}>Edit</button>
                  <button onClick={handleSave}>Save</button>
                  <button onClick={() => handleDelete(member.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AddVolunteerPoints;
