import React, { useState, useEffect } from 'react';
import './styles.css'; // Use the same CSS file for consistency

function AddVolunteerPoints() {
  const [members, setMembers] = useState([]); // To store members data
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredMembers, setFilteredMembers] = useState([]);
  const [volunteerDate, setVolunteerDate] = useState('');
  const [selectedMember, setSelectedMember] = useState(null);

  useEffect(() => {
    // Fetch members data from the backend
    fetch('/api/members')
      .then(response => response.json())
      .then(data => setMembers(data));
  }, []);

  useEffect(() => {
    setFilteredMembers(
      members.filter(member => 
        member.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        member.id.toString().includes(searchQuery)
      )
    );
  }, [searchQuery, members]);

  const handleSelectMember = (member) => {
    setSelectedMember(member);
  };

  const handleDateChange = (e) => {
    setVolunteerDate(e.target.value);
  };

  const handleAddPoints = () => {
    // Logic to add volunteer points
    console.log('Adding points for:', selectedMember, 'on', volunteerDate);
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <img src="nyclogo.jpg" alt="Nedlands Yacht Club Logo" className="logo" />
        <h1>Nedlands Yacht Club</h1>
      </nav>

      <div className="add-volunteer-container">
        {/* Search Bar */}
        <input 
          type="text" 
          placeholder="Search by ID or Name" 
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-bar"
        />

        {/* Members Table */}
        <table className="volunteer-table">
          <thead>
            <tr>
              <th>Select</th>
              <th>Member ID</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Date</th>
              <th>Maintenance Team</th>
              <th>Maintenance Event</th>
              <th>Comment</th>
              <th>Volunteering Hours</th>
              <th>Points</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredMembers.map(member => (
              <tr key={member.id}>
                <td>
                  <input 
                    type="radio" 
                    name="selectedMember" 
                    onChange={() => handleSelectMember(member)}
                  />
                </td>
                <td>{member.id}</td>
                <td>{member.firstName}</td>
                <td>{member.lastName}</td>
                <td>
                  <input 
                    type="date" 
                    value={volunteerDate} 
                    onChange={handleDateChange} 
                  />
                </td>
                <td>{/* Maintenance Team Dropdown */}</td>
                <td>{/* Maintenance Event Dropdown */}</td>
                <td><input type="text" placeholder="Comment" /></td>
                <td><input type="number" placeholder="Hours" /></td>
                <td><input type="number" placeholder="Points" /></td>
                <td><button onClick={handleAddPoints}>Add</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AddVolunteerPoints;
